import React, { useState } from "react";
/* import Form from "./Form";

var userIsRegistered = true;

function App() {
  return (
    <div className="container">
      <Form isUserRegister={userIsRegistered} />
    </div>
  );
}
 */

function App() {
  const [count, updateScore] = useState(0);

   const increase = () => {
    return updateScore(count + 1)
  }

  const decrease = () => {
    return updateScore(count - 1)
  }




  return (
    <div className="container">
      <h1>{count}</h1>
      <button onClick={increase} >+</button>
      <button onClick={decrease}>-</button>
    </div>
  );
}
export default App;
