function App() {

  const quotes = [
    {
      author: "Babe Ruth",
      quote: "It’s hard to beat a person who never gives up",
    },
    {
      author: "Les Brown",
      quote: "The harder the battle, the sweeter the victory.",
    },
    {
      author: "Michael Jordan",
      quote: "Never say never because limits, like fears, are often just an illusion",
    },
    {
      author: "Tom Landry",
      quote: "Today, you have 100 percent of your life left",
    },
    {
      author: "Vince Lombardi",
      quote: "Winning isn’t everything, but wanting to win is",
    },
  ];

  function getRandomQuote() {
    let randomNumber = Math.floor(Math.random() * quotes.length);
    console.log(randomNumber);
    let randomQuote = quotes[randomNumber];  
    console.log(randomQuote.author); 
    console.log(randomQuote.quote); 
  }


  return (
    <div className="container">
      <div className="quote">
        <p class="text"> Hello </p>
        <h4 id="author"> Mo </h4>
        <button onClick={getRandomQuote} id="tweet-quote"> New Quote </button>
      </div>
    </div>
  );

}

export default App;
