import React, { useState, useEffect } from "react";
import quotes from "./quotes"


function App() {

  const [random, setRandom] = useState({
    title: "Nelson Mandela",
    content: "The greatest glory in living lies not in never falling, but in rising every time we fall.",
    color: "black"
  });

  function getRandomQuote() {
    let randomNumber = Math.floor(Math.random() * quotes.length);
    console.log(randomNumber);
    let randomQuote = quotes[randomNumber];

    setRandom({
      title: randomQuote.author,
      content: randomQuote.quote,
      color: randomQuote.color
    });

  }



  return (
    <div className="container">
      <div className="quote" style={{ backgroundColor: random.color }}>
        <p class="text" style={{ backgroundColor: random.color }}> "{random.content}" </p>
        <h4 id="author" style={{ color: random.color }}> -{random.title} </h4>
        <button onClick={getRandomQuote} style={{ backgroundColor: random.color }} id="tweet-quote"> New Quote </button>
        <a href="https://twitter.com" target="_blank" class="btn contact-details social-icon"> <i class="fab fa-twitter end-logo"></i>
      </a>
      </div>
    </div>
  );

}

export default App;
