import React, { useState, useEffect } from "react";
import quotes from "./quotes";

function Quote() {
    const [randomQuote, setRandomQuote] = useState({
        title: "Nelson Mandela",
        content: "The greatest glory in living lies not in never falling, but in rising every time we fall.",
        color: "#C70039"
    });

    useEffect(() => { document.body.style.background = randomQuote.color });

    const getRandomQuote = () => {
        let randomNumber = Math.floor(Math.random() * quotes.length);
        let randomQuote = quotes[randomNumber];

        setRandomQuote({
            title: randomQuote.author,
            content: randomQuote.quote,
            color: randomQuote.color
        });
    }
    return (
        <div className="container">
            <div className="quote">
                <p class="text" style={{ color: randomQuote.color }}> "{randomQuote.content}" </p>
                <h4 id="author" style={{ color: randomQuote.color }}> -{randomQuote.title} </h4>
                <button onClick={getRandomQuote} style={{ backgroundColor: randomQuote.color }} id="tweet-quote"> New Quote </button>
                <a href="https://twitter.com" target="_blank" class="btn contact-details social-icon"> <i class="fab fa-twitter end-logo"></i>
                </a>
            </div>
        </div>
    )
}

export default Quote;
