import React, { useState } from "react";
/* import Form from "./Form";

var userIsRegistered = true;

function App() {
  return (
    <div className="container">
      <Form isUserRegister={userIsRegistered} />
    </div>
  );
}
 */

function App() {

  const quotes = [
    {
      author: "Babe Ruth",
      quote: "It’s hard to beat a person who never gives up",
    },
    {
      author: "Les Brown",
      quote: "The harder the battle, the sweeter the victory.",
    },
    {
      author: "Michael Jordan",
      quote: "Never say never because limits, like fears, are often just an illusion",
    },
    {
      author: "Tom Landry",
      quote: "Today, you have 100 percent of your life left",
    },
    {
      author: "Vince Lombardi",
      quote: "Winning isn’t everything, but wanting to win is",
    },
  ];

  const [random, setRandom] = useState({
    title: "",
    content: ""
  });

  function getRandomQuote() {
    let randomNumber = Math.floor(Math.random() * quotes.length);
    console.log(randomNumber);
    let randomQuote = quotes[randomNumber];

    setRandom({
      title: randomQuote.author,
      content: randomQuote.quote
    }); 

    /* let randomAuthor = randomQuote.author;
    let randomText = randomQuote.quote;
    console.log(randomAuthor);
    console.log(randomText); */
  }


  return (
    <div className="container">
      <div className="quote">
        <p class="text"> {random.content} </p>
        <h4 id="author"> ` s {random.title} ` </h4>
        <button onClick={getRandomQuote} id="tweet-quote"> New Quote </button>
      </div>
    </div>
  );

}

export default App;
